import ShowBook from "./ShowBook";

const bookCategories = [
	{ title: "Currently Reading", name: "currentlyReading" },
	{ title: "Want To Read", name: "wantToRead" },
	{ title: "Read", name: "read" },
];

const ShowBooks = ({ books, onUpdateCategory }) => {


	return (
		<div>
			{bookCategories.map((category, index) => (
				<div key={index} className="bookshelf">
					<h2 className="bookshelf-title">{category.title}</h2>
					<div className="bookshelf-books">
						<ol className="books-grid">
							{books
								.filter((book) => book.shelf === category.name)
								.map((book) => (
									<ShowBook key={book.id} book={book} handleUpdateCategory={onUpdateCategory}/>
								))}
						</ol>
					</div>
				</div>
			))}
		</div>
	);
};

export default ShowBooks;
