import { useState, useEffect } from "react";
import * as BooksAPI from "./BooksAPI";

const SearchBooks = ({ onHandleSearch }) => {
	const [books, setBooks] = useState([]);

	// const handleQuery = (event) => {
	// 	if (event.target.value !== "") {
	// 		const queryBooks = async () => {
	// 			const res = await BooksAPI.search(event.target.value, 20);
	// 			setBooks(
	// 				res.filter((book) =>
	// 					book.title
	// 						.toLowerCase()
	// 						.includes(event.target.value.toLowerCase())
	// 				)
	// 			);
	// 		};
	// 		queryBooks();
	// 	} else {
	// 		console.log("thsi is else");
	// 		setBooks([]);
	// 	}
	// };

	const handleQuery = async (event) => {
        console.log(event.target.value)
		const queryBooks = async () => {
			const res =
				event.target.value === ""
					? []
					: await BooksAPI.search(event.target.value, 20);
			setBooks(res);
		};
		queryBooks();
	};

	return (
		<div className="search-books">
			{/* {console.log("this is rendered", books, )} */}
			<div className="search-books-bar">
				<a className="close-search" onClick={() => onHandleSearch()}>
					Close
				</a>
				<div className="search-books-input-wrapper">
					<input
						onChange={handleQuery}
						type="text"
						placeholder="Search by title, author, or ISBN"
					/>
				</div>
			</div>
			<div className="search-books-results">
				starts here
				<ol className="books-grid">
					{books.map((book) => (
						<li key={book.id}>{book.title}</li>
					))}
				</ol>
			</div>
		</div>
	);
};

export default SearchBooks;
