const ShowBook = ({ book, onUpdateCategory }) => {
	const handleUpdateCategory = (event, book) => {
		event.preventDefault();
		onUpdateCategory(book, event.target.value);
	};

	return (
		<li key={book.id}>
            {console.log(book)}
			<div className="book">
				<div className="book-top">
					<div
						className="book-cover"
						style={{
							width: 128,
							height: 193,
							backgroundImage: book.imageLinks ? `url(${book.imageLinks.thumbnail})` : "none",
						}}
					></div>
					<div className="book-shelf-changer">
						<select
                            defaultValue={book.shelf ? book.shelf : "none"}
							onChange={(event) =>
								handleUpdateCategory(event, book)
							}
						>
							<option value="none" disabled>
								Move to...
							</option>
							<option value="currentlyReading">
								Currently Reading
							</option>
							<option value="wantToRead">Want to Read</option>
							<option value="read">Read</option>
						</select>
					</div>
				</div>
				<div className="book-title">{book.title}</div>
				{book.authors &&
					book.authors.map((author, index) => (
						<div key={index} className="book-authors">
							{author}
						</div>
					))}
			</div>
		</li>
	);
};

export default ShowBook;
