import { useState } from "react";
import * as BooksAPI from "./BooksAPI";

const SearchBooks = ({ onHandleSearch }) => {
	const [books, setBooks] = useState([]);

	// const handleQuery = (event) => {
	// 	const queryBooks = async () => {
	// 		const res =
	// 			event.target.value === ""
	// 				? []
	// 				: await BooksAPI.search(event.target.value, 20);
	// 		const que = await res;
	// 		setBooks(que);
	// 	};
	// 	queryBooks();
	// };

	const handleQuery = (event) => {
		const queryBooks = async () => {
			// const res =
			// 	event.target.value === ""
			// 		? []
			// 		: await BooksAPI.search(event.target.value, 20);

			const myPromise = new Promise((resolve) => {
				const res =
					event.target.value === ""
						? []
						: BooksAPI.search(event.target.value, 20);
				resolve(res);
			});

			myPromise.then((ans) => {setBooks(ans)});
		};
		queryBooks();
	};

	return (
		<div className="search-books">
			{/* {console.log("this is rendered", books)} */}
			<div className="search-books-bar">
				<a className="close-search" onClick={() => onHandleSearch()}>
					Close
				</a>
				<div className="search-books-input-wrapper">
					<input
						onChange={handleQuery}
						type="text"
						placeholder="Search by title, author, or ISBN"
					/>
				</div>
			</div>
			<div className="search-books-results">
				starts here
				<ol className="books-grid">
					{books.map((book) => (
						<li key={book.id}>{book.title}</li>
					))}
				</ol>
			</div>
		</div>
	);
};

export default SearchBooks;
