const bookCategories = [
	{ title: "Currently Reading", name: "currentlyReading" },
	{ title: "Want To Read", name: "wantToRead" },
	{ title: "Read", name: "read" },
];

const ShowBooks = ({ books, onUpdateCategory }) => {
	const handleUpdateCategory = (event, book) => {
		event.preventDefault();
		// console.log("value", event.target.value);
		// console.log(book);
        onUpdateCategory(books)
	};

	return (
		<div>
			{bookCategories.map((category, index) => (
				<div key={index} className="bookshelf">
					<h2 className="bookshelf-title">{category.title}</h2>
					<div className="bookshelf-books">
						<ol className="books-grid">
							{books
								.filter((book) => book.shelf === category.name)
								.map((book) => (
									<li key={book.id}>
										<div className="book">
											<div className="book-top">
												<div
													className="book-cover"
													style={{
														width: 128,
														height: 193,
														backgroundImage: `url(${book.imageLinks.thumbnail})`,
													}}
												></div>
												<div className="book-shelf-changer">
													<select
														onChange={(event) =>
															handleUpdateCategory(
																event,
																book
															)
														}
													>
														<option
															value="none"
															disabled
														>
															Move to...
														</option>
														<option value="currentlyReading">
															Currently Reading
														</option>
														<option value="wantToRead">
															Want to Read
														</option>
														<option value="read">
															Read
														</option>
														<option value="none">
															None
														</option>
													</select>
												</div>
											</div>
											<div className="book-title">
												{book.title}
											</div>
											{book.authors.map(
												(author, index) => (
													<div
														key={index}
														className="book-authors"
													>
														{author}
													</div>
												)
											)}
										</div>
									</li>
								))}
						</ol>
					</div>
				</div>
			))}
		</div>
	);
};

export default ShowBooks;
