import { useState } from "react";
import * as BooksAPI from "./BooksAPI";

const SearchBooks = ({ onHandleSearch }) => {
	const [books, setBooks] = useState([]);

	const handleQuery = async (event) => {
		const res =
			event.target.value === ""
				? []
				: await BooksAPI.search(event.target.value, 20);
		if (res.error) {
			setBooks([]);
		} else {
			setBooks(res);
		}
	};

	return (
		<div className="search-books">
			{console.log("this is rendered", books)}
			<div className="search-books-bar">
				<a className="close-search" onClick={() => onHandleSearch()}>
					Close
				</a>
				<div className="search-books-input-wrapper">
					<input
						onChange={handleQuery}
						type="text"
						placeholder="Search by title, author, or ISBN"
					/>
				</div>
			</div>
			<div className="search-books-results">
				starts here
				<ol className="books-grid">
					{books.map((book) => (
						<li key={book.id}>
							<div className="book">
								<div className="book-top">

										<div
											className="book-cover"
											style={{
												width: 128,
												height: 193,
												backgroundImage: book.imageLinks ? `url(${book.imageLinks.thumbnail})` : "none",
											}}
										></div>

									<div className="book-shelf-changer">
										<select defaultValue={book.shelf}>
											<option value="none" disabled>
												Move to...
											</option>
											<option value="currentlyReading">
												Currently Reading
											</option>
											<option value="wantToRead">
												Want to Read
											</option>
											<option value="read">Read</option>
											<option value="none">None</option>
										</select>
									</div>
								</div>
								<div className="book-title">{book.title}</div>
								{book.authors &&
									book.authors.map((author, index) => (
										<div
											key={index}
											className="book-authors"
										>
											{author}
										</div>
									))}
							</div>
						</li>
					))}
				</ol>
			</div>
		</div>
	);
};

export default SearchBooks;
