import "./App.css";
import { useState } from "react";
import ShowCurrentlyReading from "./ShowCurrentlyReading";
import ShowWantToRead from "./ShowWantToRead";
import ShowRead from "./ShowRead";
import AddBook from "./AddBook";
import SearchBooks from "./SearchBooks";

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);

  const updateSearchPage = () => {
      setShowSearchpage(!showSearchPage)
  }

  return (
    <div className="app">
      {showSearchPage ? (
        <SearchBooks handleSearchPage={updateSearchPage}/>
      ) : (
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <div className="list-books-content">
            <div>
              <ShowCurrentlyReading />
              <ShowWantToRead />
              <ShowRead />
            </div>
          </div>
          <AddBook handleSearchPage={updateSearchPage}/>
        </div>
      )}
    </div>
  );
}

export default App;
