import "./App.css";
import { useState, useEffect } from "react";
import AddBook from "./AddBook";
import SearchBooks from "./SearchBooks";
import * as BooksAPI from "./BooksAPI";
import ShowBooks from "./ShowBooks";

function App() {
	const [showSearchPage, setShowSearchpage] = useState(false);
    const [books, setBooks] = useState([]);

	const updateSearchPage = () => {
		setShowSearchpage(!showSearchPage);
	};

    const updateCategory = (book, shelf) => {
        const update = async () => {
            BooksAPI.update(book, shelf)
            const updatedBooks  = await BooksAPI.getAll();
            setBooks(updatedBooks)
        }

        update();
    }

	useEffect(() => {
		// const getBooks = async () => {
		// 	const res = await BooksAPI.getAll();
		// 	setBooks(res)
		// };

        const getBooks = async () => {
			const res = await BooksAPI.search("a", 100);
			setBooks(res)
            console.log(res)
		};


		getBooks();
	}, []);

	return (
		<div className="app">
			{showSearchPage ? (
				<SearchBooks onHandleSearch={updateSearchPage} books={books} onUpdateCategory={updateCategory}/>
			) : (
				<div className="list-books">
					<div className="list-books-title">
						<h1>MyReads</h1>
					</div>
					<div className="list-books-content">
						<div>
                            <ShowBooks books={books} onUpdateCategory={updateCategory}/>
						</div>
					</div>
					<AddBook
						handleSearchPage={updateSearchPage}
						books={books}
					/>
				</div>
			)}
		</div>
	);
}

export default App;
